import React, { Component } from "react";

import "./Graella.css";

let img = [
  "foto1.jpg",
  "foto2.jpg",
  "foto3.jpg",
  "foto4.jpg",
  "foto5.jpg",
  "foto6.jpg",
  "foto7.jpg",
  "foto8.jpg",
];
let i =0;
class Graella extends Component {
  constructor(props) {
    super(props);

  this.funcionZoom=this.funcionZoom.bind()(this);  

  
    
  }

  funcionZoom() {
    // Asigna los datos originales de altura y anchura
            if(i==0){
              i=i+1;
              if(document.getElementById("foto1")){document.getElementById("foto1").height='400px';document.getElementById("foto1").width='400px';}else{document.getElementById("foto1").height='100px';document.getElementById("foto1").width='100px';}
              if(this.imgRef.lastIndexof("foto2")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto3")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto4")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto5")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto6")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto7")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto8")){this.imgRef.current.clientHeight='400px';this.imgRef.current.clientWidth='400px';}else{this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
            
            }else{
              i=0;
                if(this.imgRef.lastIndexof("foto1")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto2")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto3")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto4")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto5")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto6")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto7")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
              if(this.imgRef.lastIndexof("foto8")){this.imgRef.current.clientHeight='100px';this.imgRef.current.clientWidth='100px';}
            }    
  }

  render() {
    // Asigna el actual dato de altura y anchura
    //const imgStyle = { height: this.state.height, width: this.state.width };
    return (
      <div>
        <h2>Graella de Imagenes</h2>

        <img
          style={imgStyle}
          id="foto1"
          src="foto1.jpg"
          alt="foto1"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto2"
          src="foto2.jpg"
          alt="foto2"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto3"
          src="foto3.jpg"
          alt="foto3"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto4"
          src="foto4.jpg"
          alt="foto4"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto5"
          src="foto5.jpg"
          alt="foto5"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto6"
          src="foto6.jpg"
          alt="foto6"
          onClick={this.funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto7"
          src="foto7.jpg"
          alt="foto7"
          onClick={funcionZoom()}
        />
        <img
          style={imgStyle}
          id="foto8"
          src="foto8.jpg"
          alt="foto8"
          onClick={funcionZoom()}
        />
        
      </div>
    );
  }
}
export default Graella;
