  let imgUrls = [
    "foto1.jpg",
    "foto2.jpg",
    "foto3.jpg",
    "foto4.jpg",
    "foto5.jpg",
    "foto6.jpg",
    "foto7.jpg",
    "foto8.jpg"   
 ]




class Graella extends Component{
  constructor(props){
    super(props)
     
    // Initializing states
    this.state = {height:null, width:null}
     
    // Bind context of 'this'
    this.handleZoomIn = this.handleZoomIn.bind(this)
    this.handleZoomOut = this.handleZoomOut.bind(this)
     
    // Create reference of DOM object
    this.imgRef = React.createRef()
  }
 
  componentDidMount(){
    // Saving initial dimension of image as class properties
    this.initialHeight = this.imgRef.current.clientHeight
    this.initialWidth = this.imgRef.current.clientWidth
  }
 
  // Event handler callback for zoom in
  handleZoomIn(){
   
    // Fetching current height and width
    const height = this.imgRef.current.clientHeight
    const width = this.imgRef.current.clientWidth
     
    // Increase dimension(Zooming)
    this.setState({
      height : height + 100,
      width : width + 100,
    }) 
  }
 
  // Event handler callback zoom out
  handleZoomOut(){
   
    // Assigning original height and width
    this.setState({
      height : this.initialHeight,
      width : this.initialWidth,
    })
  }
 
  render(){
    // Assign current height and width to the image
    const imgStyle = { height : this.state.height, width: this.state.width}
    return(
      <div>
        <h2>Graella de Imagenes</h2>
        
        <img style={imgStyle} ref={this.imgRef} src='foto1.jpg' alt='foto1' />
        <img style={imgStyle} ref={this.imgRef} src='foto2.jpg' alt='foto2' />
        <img style={imgStyle} ref={this.imgRef} src='foto3.jpg' alt='foto3' />
        <img style={imgStyle} ref={this.imgRef} src='foto4.jpg' alt='foto4' />
        <img style={imgStyle} ref={this.imgRef} src='foto5.jpg' alt='foto5' />
        <img style={imgStyle} ref={this.imgRef} src='foto6.jpg' alt='foto6' />
        <img style={imgStyle} ref={this.imgRef} src='foto7.jpg' alt='foto7' />
        <img style={imgStyle} ref={this.imgRef} src='foto8.jpg' alt='foto1' />
        <div>
          <button onClick={this.handleZoomIn}>Zoom In</button>
          <button onClick={this.handleZoomOut}>Zoom Out</button>
        </div>
      </div>   
    ) 
  }
}
export default Graella
