import React, { Component } from 'react';
import "./Graella.css";
function Graella() {
  return (
    <>
      <h1>GRAELLA</h1>
      <div id="menu">
        <img
          src="foto1.jpg"
          alt="1. Bebé monstruo."
          name="fotos1"
          onclick="mifoto(1)"
        />
        <img
          src="foto2.jpg"
          alt="2. Baby Yoda."
          name="fotos2"
          onclick="mifoto(2)"
        />
        <img
          src="foto3.jpg"
          alt="3. Chuky."
          name="fotos3"
          onclick="mifoto(3)"
        />
        <img
          src="foto4.jpg"
          alt="4. Maléfica."
          name="fotos4"
          onclick="mifoto(4)"
        />
        <img src="foto5.jpg" alt="5. Goku." name="fotos5" onclick="mifoto(5)" />
        <img
          src="foto6.jpg"
          alt="6. SuperMario."
          name="fotos6"
          onclick="mifoto(6)"
        />
        <img
          src="foto7.jpg"
          alt="7. Calabacita."
          name="fotos7"
          onclick="mifoto(7)"
        />
        <img
          src="foto8.jpg"
          alt="8. Superheroes."
          name="fotos8"
          onclick="mifoto(8)"
        />
      </div>
      <div id="principal">
        <div id="visor">
          <img src="foto1.jpg" alt="1. Bebé monstruo." name="fotoVisor" />
          <div id="titulo">1.Bebé monstruo.</div>
        </div>
      </div>
    </>
  );
}




  let imgUrls = [
    "foto1.jpg",
    "foto2.jpg",
    "foto3.jpg",
    "foto4.jpg",
    "foto5.jpg",
    "foto6.jpg",
    "foto7.jpg",
    "foto8.jpg"   
 ]



export default class Gallery extends Component{
  //constructor(props) {
   // super(props);
    
    state = {
      isOpen: false,
      url: ''
    }
    
//}
  
  render() {
    return(

      <div  className='container-fluid gallery-container'>
        
        <div className='row'>
          {
            imgUrls.map((url, index) => {
               return ( 
               <div key={index} className='col-sm-6 col-md-3 col-xl-2'>

                  <div className='gallery-card'>

                    <GalleryImage  className='gallery-thumbnail' src={url} alt={'Image number ' + (index + 1)} />
                    
                    <span className='card-icon-open fa fa-expand-arrows-alt' value={url} onClick={() => this.openModal(url)}></span>

                  </div>
                </div>
               )
             })
           }
        </div>
        
        <GalleryModal show={this.state.isOpen} onClick={this.closeModal} src={this.state.url} /> 

      </div>
    )
  }

      
  // Function for opening modal dialog
  // I change the function to a arrow functions to avoid to declare the binding in the constructor
  openModal = (url) => {
   
     this.setState({
       isOpen: true,
       url: url
     });
   }

  // Function for closing modal dialog
  // I change the function to a arrow functions to avoid to declare the binding in the constructor
  closeModal = () => { 
    
     this.setState({
      isOpen: false,
      url: ''
    });
  }
}
//export default Graella;
