import React, { Component } from 'react';

import "./Graella.css";


  let imgUrls = [
    "foto1.jpg",
    "foto2.jpg",
    "foto3.jpg",
    "foto4.jpg",
    "foto5.jpg",
    "foto6.jpg",
    "foto7.jpg",
    "foto8.jpg"   
 ]




class Graella extends Component{
  constructor(props){
    super(props)
     
    // Inicializar los estados
    this.state = {height:200, width:200}
     
    // Bind context of 'this'
    this.handleZoomIn = this.handleZoomIn.bind(this)
    this.handleZoomOut = this.handleZoomOut.bind(this)
     
    // Crea la referencia al objeto
    this.imgRef = React.createRef()
  }
 
  componentDidMount(){
    // Pone las dimensiones originales de la imagen
    this.initialHeight = 100
    this.initialWidth = 100
  }
 
  
  handleZoomIn(){
   
    // Crea los datos actuales de altura y anchura
    const height = this.imgRef.current.clientHeight
    const width = this.imgRef.current.clientWidth
     
    // Incrementa las dimensiones
    this.setState({
      height : height + 100,
      width : width + 100,
    }) 
  }
 
  handleZoomOut(){
   
    // Asigna los datos originales de altura y anchura
    this.setState({
      height : this.initialHeight,
      width : this.initialWidth,
    })
  }
 
  render(){
    // Asigna el actual dato de altura y anchura
    const imgStyle = { height : this.state.height, width: this.state.width}
    return(
      <div>
        <h2>Graella de Imagenes</h2>
        
        <img style={imgStyle} ref={this.imgRef} src='foto1.jpg' alt='foto1' onClick={this.handleZoomIn} />
        <img style={imgStyle} ref={this.imgRef} src='foto2.jpg' alt='foto2' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto3.jpg' alt='foto3' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto4.jpg' alt='foto4' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto5.jpg' alt='foto5' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto6.jpg' alt='foto6' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto7.jpg' alt='foto7' onClick={this.handleZoomIn}/>
        <img style={imgStyle} ref={this.imgRef} src='foto8.jpg' alt='foto1' onClick={this.handleZoomIn}/>
        <div>
          <button onClick={this.handleZoomIn}>Zoom In</button>
          <button onClick={this.handleZoomOut}>Zoom Out</button>
        </div>
      </div>   
    ) 
  }
}
export default Graella
